CREATE TABLE case_1_student_manager.courses (
    course_id INT,
    course_name VARCHAR(100) COMMENT '课程名称',
    credit FLOAT COMMENT '学分'
) COMMENT '课程信息表';

CREATE TABLE case_1_student_manager.scores (
    student_id INT,
    course_id INT,
    score INT COMMENT '得分',
    semester VARCHAR(50) COMMENT '学期'
) COMMENT '学生成绩表';

CREATE TABLE case_2_ecom.products (
    product_id INT,
    product_name VARCHAR(100) COMMENT '商品名称',
    product_price FLOAT COMMENT '商品价格'
) COMMENT '商品信息表';
